import TextField from "@material-ui/core/TextField"
import React, { useEffect, useRef, useState } from "react"
import io from "socket.io-client"
// import "./Chat.css"

function Chat() {
	const [ state, setState ] = useState({ message: "", name: "" })
	const [ chat, setChat ] = useState([])

	const socketRef = useRef()

	useEffect(
		() => {
			socketRef.current = io.connect("http://localhost:4000")
			socketRef.current.on("message", ({ name, message }) => {
				setChat([ ...chat, { name, message } ])
			})
			return () => socketRef.current.disconnect()
		},
		[ chat ]
	)

	const onTextChange = (e) => {
		setState({ ...state, [e.target.name]: e.target.value })
	}

	const onMessageSubmit = (e) => {
		const { name, message } = state
		socketRef.current.emit("message", { name, message })
		e.preventDefault()
		setState({ message: "", name })
	}

	const renderChat = () => {
		return chat.map(({ name, message }, index) => (
			<div key={index}>
				<div class="alert alert-warning" role="alert" id="message-list">{name}: <span>{message}</span></div>
			</div>
		))
	}

	return (


		<div className="card" style={{margin:'15px'}}>
			<center>

			<form onSubmit={onMessageSubmit}>
				<h1>Room</h1>
				<div className="name-field" >
					<TextField name="name" onChange={(e) => onTextChange(e)} value={state.name} label="Name" />
				</div>

				<div className="render-chat">
				{renderChat()}
				</div>


				<div class="card-footer">
                        <div class="input-group">
							<div>
								<TextField name="message" onChange={(e) => onTextChange(e)} value={state.message} label="Message..." />
								<button class="btn btn-success">SEND</button>
							</div>
                        </div>


                        <div class="input-group">
						<div>
						<TextField name="message" onChange={(e) => onTextChange(e)} value={state.message} label="Message..." />
							<TextField
								name="message"
								onChange={(e) => onTextChange(e)}
								value={state.message}
								id="outlined-multiline-static"
								variant="outlined"
								label="Message"
								/>
                            <button class="btn btn-success">SEND</button>
                        </div>

						<div class="input-group">
                            <input type="text" name="message" onChange={(e) => onTextChange(e)} value={state.message}  placeholder="Type a message..."/>
                            <div class="input-group-append">
                                <button class="btn btn-success" type="button" id="messageBtn">Send</button>
                            </div>
                        </div>


                        </div>
                    </div>  
			</form>
								</center>
		</div>
	)
}

export default Chat
