import { Component } from 'react';
import { Link } from 'react-router-dom';

export default class Room extends Component {
    constructor(props) {
        super(props);
        this.state = { code: props.code, 
                        subject:'' }
    }
    componentDidMount() {
        const requestOption = {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' }
        }
        fetch('http://localhost:8000/getRoom/' + this.state.code, requestOption)
            .then(res => res.json())
            .then(data => this.setState({ subject: data.subject,owner }))

    }
    render() {
        // const {code} = this.props;
        return (
            <section>
                <div className="card col" style={{ width: '18rem', margin: '35px' }}>
                    <img className="card-img-top" src={process.env.PUBLIC_URL + '/images/dog.png'} style={{ width: '100%' }} alt="Card image cap" />
                    <div className="card-body">
                        <center>
                            <h5 className="card-title">{this.state.subject}</h5>
                            <p className="card-text">{this.state.owner}</p>

                            <Link to="/" className="btn btn-primary">Go to  {this.state.code}</Link>
                        </center>
                    </div>
                </div>


            </section>
        )
    }
}
