import React, { Component } from "react";
import { Redirect } from "react-router-dom";

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "e-mail",
            password: "password",
            successfulPOST: this.state,
        };
        this.handleChange = this.handleChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    handleChange(event) {
        if (event.target.name === "email") {
            this.setState({ email: event.target.value });
        } else {
            this.setState({ password: event.target.value });
        }
    }

    // submit login//
    onSubmit(event) {
        event.preventDefault("");
        fetch("http://localhost:8000/login", {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                email: this.state.email,
                password: this.state.password,
            }),
        }).then((res) => res.json());
        this.setState({ successfulPOST: true });
    }

    render() {
        if (this.state.successfulPOST === true) {
            return <Redirect push to={{ pathname: '/home' }} />
        }
        else {
            return (
                <div className="card">
                    <div>
                        <div className="card">
                            <h1 className="headtext">LOGIN</h1>
                            <div className="card-body">
                                <form>
                                    <div className="mb-3">
                                        <label htmlFor="exampleInputEmail1" className="form-label">
                                            Email address
                        </label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            id="InputEmail1"
                                            aria-describedby="emailHelp"
                                            onChange={this.handleChange}
                                            placeholder="email"
                                            name="email"
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="exampleInputPassword1" className="form-label">
                                            Password
                        </label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            id="InputPassword"
                                            placeholder="Password"
                                            name="password"
                                            onChange={this.handleChange}
                                        />
                                    </div>

                                </form>
                                <div className="btn">
                                    <a
                                        className="btn btn-primary me-md-2"
                                        type="button"
                                        style={{ marginRight: "40px" }}
                                        onClick={this.onSubmit}>
                                        LOGIN</a>
                                    <a className="btn btn-primary" href="/register" role="button">
                                        REGISTER
                      </a>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }





    }
}
